import users from "../../../Users/Users";
import {useNavigate } from "react-router-dom";

function Modal({contacts, setContactList, MyUsername}) {
    const history = useNavigate();
    function addContact() {
        const Username = document.querySelector('#userToAdd').value;

        if (!Username) {
            alert('Please enter a username');
            return;
        }
        const user = users.find(user => user.username === Username);
        if (!user) {
            alert('User not found');       
            return;

        }
        const cont = contacts.find(cont => cont.username === Username)
        if(cont){
            alert('User already in contacts');
            return;
        }
        const toAdd = {username:user.username, display:user.display, date:"", img:user.image, active: "0", messages: []};
        setContactList((prevContacts) => [...prevContacts, toAdd]);
        history(`/Chats?name=${MyUsername}`);
    }


    return (
        <>
            <div
                className="modal fade"
                id="exampleModal"
                tabIndex={-1}
                aria-labelledby="exampleModalLabel"
                aria-hidden="true">
                <div className="modal-dialog modal-dialog-centered">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h1 className="modal-title fs-5" id="exampleModalLabel">
                                Add contact
                            </h1>
                            <button
                                type="button"
                                className="btn-close"
                                data-bs-dismiss="modal"
                                aria-label="Close"
                            />
                        </div>
                        <div className="modal-body">
                            <div className="input-group mb-3">
                                <span className="input-group-text" id="basic-addon1">
                                    Username
                                </span>
                                <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Username"
                                    aria-label="Username"
                                    aria-describedby="basic-addon1"
                                    id="userToAdd"
                                />
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-primary" onClick={addContact}>
                                Add
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Modal
