import users from "../Users/Users";

const checkCredentials = (username) => {
    const foundUser = users.find(user => user.username === username)
    return foundUser !== undefined;
};

const checkCredentials2 = (displayName) => {
    const foundDisplay = users.find(user => user.display === displayName)
    return foundDisplay !== undefined;
};
export {checkCredentials, checkCredentials2};
