import React, { useEffect } from 'react';
import Contact from '../Contact/Contact';
async function getMsg(id, token) {
  console.log(id);
  const response = await fetch('http://localhost:5000/api/Chats/' + id + '/Messages', {
    method: 'Get',
    headers: {
      'Content-type': 'application/json',
      'Authorization': 'Bearer ' + token,
    },
  });

  if (response.ok) {
    const msgArr = await response.json();
    console.log(msgArr);
    return msgArr;
  } else {
    throw new Error('Invalid username or password');
  }
}

function ContactListResult({ contacts, setContactList, token }) {
  
  const deleteContact = async (id) => {
  
    const response = await fetch('http://localhost:5000/api/Chats/' + id, {
      method: 'DELETE',
      headers: {
        'Content-type': 'application/json',
        'Authorization': 'Bearer ' + token,
      },
    });
    if (response.ok) {
     
      // Update the contacts list after deleting the contact
      const updatedContacts = contacts.filter(contact => contact.id !== id);
      setContactList(updatedContacts);
    }
  };

  const attachEventListeners = () => {
    const chats = document.getElementsByClassName('chats');

    // Add a click event listener to each button
    for (let i = 0; i < chats.length; i++) {
      chats[i].addEventListener('click', function () {
        // Remove the "active" class from all buttons
        for (let j = 0; j < chats.length; j++) {
          if (chats[j].classList.contains('active')) {
            chats[j].classList.remove('active');
          }
        }

        // Add the "active" class to the button that was pressed
        this.classList.add('active');
      });
    }
  };




  const attachEventListenersMsg = () => {
    const chats = document.getElementsByClassName('chats');

    // Add a click event listener to each button
    for (let i = 0; i < chats.length; i++) {
      chats[i].addEventListener('click', function () {
        // Remove the "active" class from all buttons
        for (let j = 0; j < chats.length; j++) {
          if (chats[j].classList.contains('active')) {
            getMsg(id,token);
          }
        }
      });
    }
  };
  // Attach event listeners after the component renders
  useEffect(() => {
    attachEventListeners();
  }, [contacts]);

  const userMap = new Map();
  const uniqueContacts = [];

  // Iterate over contacts to extract unique users
  for (let i = 0; i < contacts.length; i++) {
    const contact = contacts[i];
    const userId = contact.id;
    const user = contact.user;

    // Check if the user is already added to the map
    if (!userMap.has(userId)) {
      userMap.set(userId, user);
      uniqueContacts.push(contact);
    }
  }

  const contactList = uniqueContacts.map((contact) => (
    <Contact {...contact.user} key={contact.id} token={token} id={contact.id} deleteContact={deleteContact} />
  ));

  return <div className="users_list">{contactList}</div>;
}

export default ContactListResult;
