  console.log("/////////////////////////////////////////////////////////////////////////////////////////");
  console.log(id);
  const response = await fetch('http://localhost:5000/api/Chats/' + id + '/Messages', {
    method: 'Get',
    headers: {
      'Content-type': 'application/json',
      'Authorization': 'Bearer ' + token,
    },
  });

  if (response.ok) {
    const msgArr = await response.json();
    console.log(msgArr);
    return msgArr;
  } else {
    throw new Error('Invalid username or password');
  }
}

