function Contact({ display, date, img, active }) {
    let image = img;
    if (img instanceof File) {
      image = URL.createObjectURL(img);
    }
  
    if (active === "0") {
      return (
        <div className="row-aaa chats">
          <img className="side-img" src={image} alt={display} />
          <p className="contact_name">{display}</p>
          <p className="date">{date}</p>
        </div>
      );
    } else {
      return (
        <div className="row-aaa chats active">
          <img className="side-img" src={img} alt={display} />
          <p className="contact_name">{display}</p>
          <p className="date">{date}</p>
        </div>
      );
    }
  }
  
  export default Contact;
