async function deleteContact(id, token) {
  console.log("try")
  const response = await fetch('http://localhost:5000/api/Tokens', {
      method: 'DELETE',
      headers: {
        'Content-type': 'application/json',
        'Authorization': 'Bearer ' + token,
      },
    });
    if (response.ok) {
      console.log("deleted");
    }
}




function Contact({ displayName, profilePic, username, token}) {
    let image = profilePic;
    if (profilePic instanceof File) {
      image = URL.createObjectURL(profilePic);
    }
    let active = 0;
    if (active === 0) {
      return (
        <div className="row-aaa chats">
          <img className="side-img" src={image} alt={displayName} />
          <p className="contact_name">{displayName}</p>
          <p className="date">1.1.1.1.1</p>
          <i className="bi bi-x-square" onClick={() => deleteContact("1", token)}></i>
          </div>
      );
    } else {
      return (
        <div className="row-aaa chats active">
          <img className="side-img" src={profilePic} alt={displayName} />
          <p className="contact_name">{displayName}</p>
          <p className="date">1.1.1.1.1</p>
          <i className="bi bi-x-square" onClick={() => deleteContact("1", token)}></i>
        </div>
      );
    }
  }
  
  export default Contact;
