import './Right.css'
import ActiveContact from './ActiveContact/ActiveContact';
import ActiveChat from './ActiveChat/ActiveChat';
import React, { useState } from 'react';

function convertToDate(timeString) {
  if(timeString === ''){
    return new Date(0);
  }
  const [datePart, timePart] = timeString.split(', ');
  const [day, month, year] = datePart.split('.');
  const [hour, minute, second] = timePart.split(':');

  return new Date(`${month}/${day}/${year} ${hour}:${minute}:${second}`);
}


function Right({ p, image, contactList, setContactList,token, chatID }) {

  const [inputValue, setInputValue] = useState('');
  const contact = contactList.find(obj => obj.display === p);
  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleButtonClick();
    }
  };

  const handleButtonClick = () => {
    if (inputValue !== '') {
      const date = new Date();
      const hours = date.getHours();
      const minutes = date.getMinutes();
      const formattedTime = `${hours}:${minutes < 10 ? '0' : ''}${minutes}`;
      const newMessage = { text: inputValue, time: formattedTime, classname: 'message-box my-message' };
  
      // Update the contact's messages array by creating a new array with the new message
      const updatedContact = { ...contact, date: `${date.toLocaleDateString()}, ${date.toLocaleTimeString()}`,  messages: [...contact.messages, newMessage] };
  
      // Find the index of the contact in the contactList array
      const contactIndex = contactList.findIndex(obj => obj.display === p);
  
      // Create a new contactList array with the updated contact
      const updatedContactList = [...contactList];
      updatedContactList[contactIndex] = updatedContact;
  
      // Update the contactList state with the updated array
      setContactList(updatedContactList);
  
      setInputValue('');
  
      // Get the active chat element
      const activeContact = document.querySelector('.active');
      const dateElement = activeContact.querySelector('.date');
      const formattedDate = `${date.toLocaleDateString()}, ${date.toLocaleTimeString()}`;
      dateElement.innerHTML = formattedDate;
  

      updatedContactList.sort((a, b) => {
        const timeA = convertToDate(a.date);
        const timeB = convertToDate(b.date);
      
        return timeB - timeA;
      });
      console.log(updatedContactList);
      setContactList(updatedContactList);
    }
  };





  return (
    <>
    <ActiveContact name={p} img={image} />
    {contact && (
      <>
        <ActiveChat Messages={contact.messages} token={token} chatID={chatID}/>
        <div className="row-aaa fff" id="footer">
          <input type="text" placeholder="Type a message" id="message-input" value={inputValue} onChange={handleInputChange} onKeyPress={handleKeyPress}/>
          <button type="button" className="btn btn-info" onClick={handleButtonClick}>
            <i className="bi bi-send-fill" />
          </button>
        </div>
      </>
    )}
  </>
  );
}

export default Right;
