async function getMsg(id, token) {

  const response = await fetch('http://localhost:5000/api/Chats/' + id + '/Messages', {
    method: 'Get',
    headers: {
      'Content-type': 'application/json',
      'Authorization': 'Bearer ' + token,
    },
  });

  if (response.ok) {
    const msgArr = await response.json();
    console.log(msgArr);
    return msgArr;
  } else {
    throw new Error('Invalid username or password');
  }
}

function Contact({ displayName, profilePic, username, token, id, deleteContact }) {
  console.log(id);
  let image = profilePic;
  if (profilePic instanceof File) {
    image = URL.createObjectURL(profilePic);
  }
  let active = 0;

  const handleDelete = (event) => {
    event.stopPropagation(); // Stop event propagation
    deleteContact(id); // Call the deleteContact function
  };

  if (active === 0) {
    getMsg(id, token);

    return (
      <div className="row-aaa chats">
        <img className="side-img" src={image} alt={displayName} />
        <p className="contact_name">{displayName}</p>
        <p className="date">1.1.1.1.1</p>
        <i className="bi bi-x-square" onClick={handleDelete}></i>
      </div>
    );
  } else {
    getMsg(id, token);
    return (
      <div className="row-aaa chats active">
        <img className="side-img" src={profilePic} alt={displayName} />
        <p className="contact_name">{displayName}</p>
        <p className="date">2.2.2.2.2</p>
        <i className="bi bi-x-square" onClick={handleDelete}></i>
      </div>
    );
  }
}

export default Contact;
